/// <reference types="node" />
import * as http from 'http';
/**
 * Mix of different standarized HTTP-Response Codes
 * The 200-code is dropped, as it's returned as default in NodeJS
 *
 * The http.ServerResponse is required for each method, to assign the statusCode and statusMessage to the current response
 * If a responseText is appended as a parameter, the request will be ended as well
 */
export declare class HttpResponse {
    private static EndResponse;
    static Created(res: http.ServerResponse, location?: string, responseText?: string): void;
    static NoContent(res: http.ServerResponse, responseText?: string): void;
    static BadRequest(res: http.ServerResponse, responseText?: string): void;
    static Unauthorized(res: http.ServerResponse, responseText?: string): void;
    static Forbidden(res: http.ServerResponse, responseText?: string): void;
    static NotFound(res: http.ServerResponse, responseText?: string): void;
    static MethodNotAllowed(res: http.ServerResponse, supportedMethods: string[]): void;
    static ServerError(res: http.ServerResponse, responseText?: string): void;
}
